select.if:
SELECT p.`name`, p.url, p.id FROM `t_perms` AS p LEFT JOIN t_role_perms AS rp ON p.id = rp.perms_id WHERE p.parent_id = '0'
AND rp.role_id = #{roleId}
GROUP BY p.sort

SELECT p.`name`, p.url, p.id FROM t_perms AS p LEFT JOIN t_role_perms AS rp ON p.id = rp.perms_id WHERE p.parent_id = #{parentId}
AND rp.role_id = #{roleId}
GROUP BY p.sort
